SELECT * FROM activity_config
WHERE status=1

  AND englishTitle=#{title}


  AND entrance like "%"#{entrance}"%"


  AND level=#{level}

SELECT * FROM activity_config
WHERE status=1

  AND englishTitle=#{title}


  AND entrance like "%"#{entrance}"%"


  AND level=#{level}


           and  activityEndTime  >= #{startTime,jdbcType=TIMESTAMP} and 
             activityStartTime  <=    #{endTime,jdbcType=TIMESTAMP}

select  id, loginname,  amount, status, distribute_month,starttime,endtime,depositAmount,remark     from privilege 


    and  status = #{status,jdbcType=VARCHAR}


    and  loginname = #{loginname,jdbcType=VARCHAR}


    and remark = #{remark,jdbcType=VARCHAR}


    and  distribute_month = #{distribute_month,jdbcType=VARCHAR}

select  sum(amount)  from proposal 


       and flag = '1'


      and   loginname = #{loginname,jdbcType=VARCHAR}


       and  type = #{pType,jdbcType=VARCHAR}



       and createTime >= #{startTime,jdbcType=TIMESTAMP} and 
         createTime <=    #{endTime,jdbcType=TIMESTAMP}

select sum(money) from payorder  


        and   loginname = #{loginname,jdbcType=VARCHAR}


         and  type = #{orderType,jdbcType=VARCHAR}


         and createTime >= #{startTime,jdbcType=TIMESTAMP} and 
           createTime <=    #{endTime,jdbcType=TIMESTAMP}
